import { Component, EventEmitter, Input, Output } from '@angular/core';
const FILTER_PAG_REGEX = /[^0-9]/g;

@Component({
  selector: 'dt-table-pagination',
  templateUrl: './dtTablePagination.component.html',
  styleUrls: ['./dtTablePagination.component.css']
})
export class DtTablepaginationComponent {
  constructor() { }
  @Output()
  pagChange = new EventEmitter();

  @Input()
  totalLenght: number;

  public page = 0;

  formatInput(input: HTMLInputElement) {
    input.value = input.value.replace(FILTER_PAG_REGEX, '');
  }

  selectPage(page: string) {
    this.page = parseInt(page, 10) || 1;
  }

  onEmitChange(event: any) {
    this.pagChange.emit(event);
  }

}
